INSERT INTO `profils` (`id`, `selayang`, `visi`, `updated_at`, `created_at`, `del`, `foto`, `struktur`) VALUES
(1, '<p><b>KEDUDUKAN :</b></p>\r\n\r\n<p>Dinas Pekerjaan Umum Sumber Daya Air Provinsi Jawa Timur merupakan unsur pelaksana otonomi daerah, dipimpin oleh seorang Kepala Dinas, yang berada di bawah dan bertanggung jawab kepada Gubernur melalui Sekretaris Daerah. Mempunyai tugas membantu Gubernur melaksanakan urusan pemerintahan yang menjadi kewenangan Pemerintah Provinsi di bidang pekerjaan umum sumber daya air dan tugas pembantuan.</p>\r\n\r\n<p><b>ALAMAT :</b></p>\r\n\r\n<p>Jl. Gayung Kebonsari No. 169 Surabaya</p>\r\n\r\n<p><b>MOTTO :</b></p>\r\n\r\n<p>“ MEMAYU HAYUNING TIRTO ”</p>\r\n\r\n<p>Artinya :</p>\r\n\r\n<p>Usaha Mempertahankan Kelestarian Sumber Daya Air</p>', '<p><strong>Visi :</strong></p>\r\n\r\n<p>Terwujudnya pengelolaan sumber daya air yang menyeluruh, terpadu dan berwawasan lingkungan untuk mendukung Jawa Timur lebih sejahtera.</p>\r\n\r\n<p><strong>Misi :</strong></p>\r\n\r\n<ol>\r\n	<li>Meningkatkan konservasi sumber daya air yang berkelanjutan.</li>\r\n	<li>Meningkatkan pendayagunaan sumber daya air secara adil untuk berbagai kebutuhan.</li>\r\n	<li>Mengendalikan dan mengurangi resiko daya rusak air.</li>\r\n</ol>\r\n\r\n<p><strong>Tugas dan Fungsi :</strong></p>\r\n\r\n<p>Dinas Pekerjaan Umum Sumber Daya Air Provinsi Jawa Timur mempunyai tugas membantu Gubernur melaksanakan urusan pemerintahan yang menjadi kewenangan Pemerintah Provinsi di bidang pekerjaan umum sumber daya air dan tugas pembantuan.</p>\r\n\r\n<p>Dinas Pekerjaan Umum Sumber Daya Air Provinsi Jawa Timur dalam melaksanakan tugas sebagaimana dimaksud, menyelenggarakan fungsi:</p>\r\n\r\n<p>a. perumusan kebijakan di bidang pekerjaan umum sumber daya air;</p>\r\n\r\n<p>b. pelaksanaan kebijakan di bidang pekerjaan umum sumber daya air;</p>\r\n\r\n<p>c. pelaksanaan evaluasi dan pelaporan di bidang pekerjaan umum sumber daya air;</p>\r\n\r\n<p>d. pelaksanaan administrasi Dinas di bidang pekerjaan umum sumber daya air; dan</p>\r\n\r\n<p>e. pelaksanaan fungsi lain yang diberikan oleh Gubernur terkait dengan tugas dan fungsinya.</p>\r\n', '2023-09-12 06:46:39', '0000-00-00 00:00:00', '0', NULL, NULL),
(2, ' Ir. BAJU TRIHAKSORO, M.M.', '19650823 199403 1 008', '2023-09-12 06:56:37', '0000-00-00 00:00:00', '0', 'jzqjP47Z9W-SO PU SDA 2023.jpg', '');
